func triangle(n int) {
    var item []int
    for i := 1; i &lt;= n; i++ {
        item_len := len(item)
        if item_len == 0 {
            item = append(item, 1)
        } else {
            temp_s := []int{1}
            for j := 0; j &lt; item_len-1; j++ {
                temp_s = append(temp_s, item[j]+item[j+1])
            }
            temp_s = append(temp_s, 1)
            item = temp_s
        }
        fmt.Println(item)
    }
}

