嵌套循环实现goto语句实现 Go<pre><code>package main

import "fmt"

func main() {
    for m := 1; m &lt; 10; m++ {
        /*    fmt.Printf("第%d次：\n",m) */
        for n := 1; n &lt;= m; n++ {
            fmt.Printf("%dx%d=%d ", n, m, m*n)
        }
        fmt.Println("")
    }
}
</code></pre> Go<pre><code>package main

import "fmt"

func main() {
    //print9x()
    gotoTag()
}

//嵌套for循环打印九九乘法表
func print9x() {
    for m := 1; m &lt; 10; m++ {
        for n := 1; n &lt;= m; n++ {
            fmt.Printf("%dx%d=%d ", n, m, m*n)
        }
        fmt.Println("")
    }
}

//for循环配合goto打印九九乘法表
func gotoTag() {
    for m := 1; m &lt; 10; m++ {
        n := 1
    LOOP:
        if n &lt;= m {
            fmt.Printf("%dx%d=%d ", n, m, m*n)
            n++
            goto LOOP
        } else {
            fmt.Println("")
        }
        n++
    }
}
