package main

import (
    "bufio"
    "fmt"
    "net"
    "os"
)

func ClientHandleError(err error, when string){
    if err != nil {
        fmt.Println(err, when)
        os.Exit(1)
    }
}

func main(){
    //拨号远程地址，简历tcp连接
    conn, err := net.Dial("tcp","127.0.0.1:8888")
    ClientHandleError(err, "client conn error")

    //预先准备消息缓冲区
    buffer := make([]byte,1024)

    //准备命令行标准输入
    reader := bufio.NewReader(os.Stdin)

    for {
        lineBytes,_,_ := reader.ReadLine()
        conn.Write(lineBytes)
        n,err := conn.Read(buffer)
        ClientHandleError(err, "client read error")

        serverMsg := string(buffer[0:n])
        fmt.Printf("服务端msg",serverMsg)
        if serverMsg == "bye" {
            break
        }
    }
}
</code></pre> C<pre><code>#include &lt;strings.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;stdio.h&gt;
#include &lt;sys/types.h&gt;          /* See NOTES */
#include &lt;sys/socket.h&gt;
#include &lt;netinet/ip.h&gt;
#include &lt;unistd.h&gt;
#include &lt;arpa/inet.h&gt;
#include &lt;pthread.h&gt;

void *recv_data(void *arg)
{
    int sockfd = *(int *)arg;
    char s[64] = {0};
    while(1)
    {
        int ret = read(sockfd, s, 64);
        if(ret &lt; 0)
        {
            perror("read");
            return NULL;
        }
        printf("%s\n", s);
        memset(s, 0, 64);
    }
}

int main(int argc, char *argv[])
{
    int sockfd = socket(AF_INET, SOCK_STREAM, 0);
    if(sockfd == -1)
    {
        perror("socket");
        return -1;
    }

    pthread_t pid;
    pthread_create(&amp;pid, NULL, recv_data, (void *)&amp;sockfd);
    pthread_detach(pid);

    struct sockaddr_in ser;
    ser.sin_family = AF_INET;
    ser.sin_port = htons(11111);
    //ser.sin_addr.s_addr = inet_addr("0.0.0.0");
    ser.sin_addr.s_addr = htonl(INADDR_ANY);

    int ret = connect(sockfd, (struct sockaddr *)&amp;ser, sizeof(ser));
    if(ret == -1)
    {
        perror("connect");
        return -1;
    }

    char buf[64];
    while(1)
    {
        fgets(buf, 64, stdin);
        buf[strlen(buf)-1] = '\0';
        write(sockfd, buf, 64);

        if(strcmp(buf, "time") == 0)
        {
            char data[64];
            read(sockfd, data, 64);
            printf("&gt;&gt;:%s\n", data);
            memset(buf, 0, 64);
            memset(data, 0, 64); 
        } else if(strncmp(buf, "get", 3) == 0) {
            char str[64];
            while(read(sockfd, str, 64) != 0) {
                if(strcmp(str, "over") == 0) {
                    memset(str, 0, 64);        
                    break;
                }
                printf("&gt;&gt;:%s\n", str);
                memset(buf, 0, 64);
                memset(str, 0, 64); 
            }
        } else {	
            char arr[64] = {0};
            read(sockfd, arr, 64);
            printf("&gt;&gt;:%s\n", arr);
            memset(buf, 0, 64);
            memset(arr, 0, 64); 
        }
    }
    close(sockfd);
    return 0;
}
